import React, { useEffect } from "react";
import useCookie from "../hooks/useCookie";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { GoogleAuthProvider, getRedirectResult } from "firebase/auth";
import { auth } from "../google/Config";
import { setAccount } from "../../redux/slice/account";

const Home = () => {
  const { removeCookie } = useCookie();
  const dispatch = useDispatch();
  const selector = useSelector((state) => state.account);
  const account = selector.account;
  const navigate = useNavigate();
  const handleSignout = () => {
    removeCookie();
    navigate("/login");
  };
  useEffect(() => {
    getRedirectResult(auth)
      .then((result) => {
        // This gives you a Google Access Token. You can use it to access Google APIs.

        // The signed-in user info.
        dispatch(setAccount(result.user.providerData));
        // IdP data available using getAdditionalUserInfo(result)
        // ...
      })
      .catch((error) => {
        // Handle Errors here.
        const errorCode = error.code;
        const errorMessage = error.message;
        // The email of the user's account used.
        const email = error.customData.email;
        // The AuthCredential type that was used.
        const credential = GoogleAuthProvider.credentialFromError(error);
        // ...
      });
  }, [account]);
  return (
    <div>
      <h1>Home</h1>
      <div className="  h-[150px] w-[150px] rounded-full border-[1px] border-black relative">
        <img
          src={account[0]?.photoURL}
          alt=""
          className=" absolute w-full h-full rounded-full"
        />
      </div>
      <p>
        Email:<span>{account[0]?.email}</span>
      </p>
      <p>
        Username:<span>{account[0]?.displayName}</span>
      </p>
      <button
        onClick={handleSignout}
        className=" pt-1 pb-1 pl-2 pr-2 border-[1px] cursor-pointer border-black rounded-[5px] hover:bg-black hover:text-white"
      >
        Signout
      </button>
    </div>
  );
};

export default Home;
